module.exports = async (params) => {
    const app = params.app;

    // Prompt user for source and target files
    const sourceFile = await app.plugins.plugins["quickadd"].api.fileSuggester("Select the source file");
    if (!sourceFile) {
        new Notice("No source file selected");
        return;
    }

    const targetFile = await app.plugins.plugins["quickadd"].api.fileSuggester("Select the target file");
    if (!targetFile) {
        new Notice("No target file selected");
        return;
    }

    // Read contents of the source file
    const sourceContents = await app.vault.read(sourceFile);
    const sourceFrontmatter = sourceContents.match(/^---\n(.*?)\n---\n(.*)$/s);
    if (!sourceFrontmatter) {
        new Notice("No frontmatter found in source file");
        return;
    }

    const sourceTagsMatch = sourceFrontmatter[1].match(/tags:\s*\[(.*?)\]/s);
    if (!sourceTagsMatch) {
        new Notice("No tags found in source file");
        return;
    }

    const sourceTags = sourceTagsMatch[1].split(",").map(tag => tag.trim()).filter(tag => tag);

    if (sourceTags.length === 0) {
        new Notice("No tags to duplicate in source file");
        return;
    }

    // Read contents of the target file
    const targetContents = await app.vault.read(targetFile);
    const targetFrontmatter = targetContents.match(/^---\n(.*?)\n---\n(.*)$/s);
    if (!targetFrontmatter) {
        new Notice("No frontmatter found in target file");
        return;
    }

    const targetTagsMatch = targetFrontmatter[1].match(/tags:\s*\[(.*?)\]/s);
    let targetTags = [];
    if (targetTagsMatch) {
        targetTags = targetTagsMatch[1].split(",").map(tag => tag.trim()).filter(tag => tag);
    }

    // Filter out tags that already exist in the target file
    const newTags = sourceTags.filter(tag => !targetTags.includes(tag));

    if (newTags.length === 0) {
        new Notice("No new tags to add");
        return;
    }

    // Update target tags
    const allTags = [...targetTags, ...newTags];
    const newTagsString = allTags.join(", ");

    const newTargetFrontmatter = targetFrontmatter[1].replace(targetTagsMatch ? targetTagsMatch[0] : 'tags: []', `tags: [${newTagsString}]`);
    const newTargetContents = targetContents.replace(targetFrontmatter[0], `---\n${newTargetFrontmatter}\n---\n${targetFrontmatter[2]}`);

    await app.vault.modify(targetFile, newTargetContents);
    new Notice("Tags duplicated successfully");
};
